import { KULUPU_GENESIS, KUSAMA_GENESIS, POLKADOT_GENESIS } from "../constants.js";
const PROPOSE_THRESHOLDS = {
  [KULUPU_GENESIS]: 1,
  [KUSAMA_GENESIS]: 0.5,
  [POLKADOT_GENESIS]: 0.6,
  default: 0.5
};
const SLASH_THRESHOLDS = {
  [KUSAMA_GENESIS]: 0.5,
  [POLKADOT_GENESIS]: 0.75,
  default: 0.5
};
const TREASURY_THRESHOLDS = {
  [KULUPU_GENESIS]: 0.5,
  [KUSAMA_GENESIS]: 0.6,
  [POLKADOT_GENESIS]: 0.6,
  default: 0.6
};
export function getProposalThreshold(api) {
  return PROPOSE_THRESHOLDS[api.genesisHash.toHex()] || PROPOSE_THRESHOLDS.default;
}
export function getSlashProposalThreshold(api) {
  return SLASH_THRESHOLDS[api.genesisHash.toHex()] || SLASH_THRESHOLDS.default;
}
export function getTreasuryProposalThreshold(api) {
  return TREASURY_THRESHOLDS[api.genesisHash.toHex()] || TREASURY_THRESHOLDS.default;
}